from robolink import *
from robodk import *

RDK = Robolink()

def reset():

    robot = RDK.Item('KR-30-3')
    robot.setJoints([0,0,0,0,0,0])
    print("Reset KR-30-3")


def addGeo():
    points = []
    points.append([1000, 0 , -1000])
    points.append([1000, 1000 , -1000])
    points.append([0, 1000 , -1000])
    
    RDK.AddShape(points)

def touchTargets():
    robot = RDK.Item('KR-30-3')
    robot.setJoints([0,0,0,0,0,0])

    target = RDK.Item('Target 1')         # retrieve the Target item
    robot.MoveJ(target)   

    target = RDK.Item('Target 2')         # retrieve the Target item
    robot.MoveL(target)   


